// Stats.js
import React from 'react';
import { Bar } from 'react-chartjs-2';

const Stats = () => {
  // Sample data for demonstration purposes
  const data = {
    labels: ['Agent 1', 'Agent 2', 'Agent 3', 'Agent 4', 'Agent 5'],
    datasets: [
      {
        label: 'Win Rate (%)',
        data: [55, 60, 70, 65, 50],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h2 className="center-text">Team Statistics</h2>
      <div className="chart-container">
        <Bar data={data} options={options} />
      </div>
      <div className="table-container">
        <h3>Player Statistics</h3>
        <table>
          <thead>
            <tr>
              <th>Player</th>
              <th>Agent</th>
              <th>Win Rate (%)</th>
              <th>Playstyle</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Player 1</td>
              <td>Agent 1</td>
              <td>55</td>
              <td>Aggressive</td>
            </tr>
            <tr>
              <td>Player 2</td>
              <td>Agent 2</td>
              <td>60</td>
              <td>Defensive</td>
            </tr>
            <tr>
              <td>Player 3</td>
              <td>Agent 3</td>
              <td>70</td>
              <td>Support</td>
            </tr>
            <tr>
              <td>Player 4</td>
              <td>Agent 4</td>
              <td>65</td>
              <td>Balanced</td>
            </tr>
            <tr>
              <td>Player 5</td>
              <td>Agent 5</td>
              <td>50</td>
              <td>Aggressive</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Stats;
