import React, { useState, useEffect } from 'react';

const Stats = ({ playerId }) => {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch(`https://na1.api.riotgames.com/val/stats/v1/players/${encodeURIComponent(playerId)}`, {
          headers: {
            'Authorization': `Bearer ${process.env.REACT_APP_RIOT_API_KEY}`
          }
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('API Response:', data); // Log the response
        setStats(data);
      } catch (error) {
        console.error('Failed to fetch:', error);
      }
    };

    fetchStats();
  }, [playerId]);

  if (!stats) return <div>Loading...</div>;

  return (
    <div>
      <h2>{stats.playerName}</h2>
      <p>Rank: {stats.rank}</p>
      <p>KDA: {stats.kda}</p>
      <p>ADR: {stats.adr}</p>
      {/* Add more stats as needed */}
    </div>
  );
};

export default Stats;
