import React, { useState, useEffect } from 'react';

const Stats = ({ playerId }) => {
  const [stats, setStats] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      const response = await fetch(`https://api.valtracker.gg/stats/${playerId}`, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_VAL_TRACKER_API_KEY}`
        }
      });
      const data = await response.json();
      setStats(data);
    };

    fetchStats();
  }, [playerId]);

  if (!stats) return <div>Loading...</div>;

  return (
    <div>
      <h2>{stats.playerName}</h2>
      <p>Rank: {stats.rank}</p>
      <p>KDA: {stats.kda}</p>
      <p>ADR: {stats.adr}</p>
      {/* Add more stats as needed */}
    </div>
  );
};

export default Stats;
