import React, { useState, useEffect } from 'react';

const CompareStats = ({ playerIds }) => {
  const [stats, setStats] = useState([]);

  useEffect(() => {
    // Fetch stats for each player
    Promise.all(playerIds.map(id => fetch(`https://api.valtracker.gg/stats/${id}`).then(res => res.json())))
      .then(data => setStats(data));
  }, [playerIds]);

  if (stats.length === 0) return <div>Loading...</div>;

  return (
    <div>
      <h2>Compare Stats</h2>
      {stats.map((stat, index) => (
        <div key={index}>
          <h3>{stat.playerName}</h3>
          <p>Rank: {stat.rank}</p>
          <p>KDA: {stat.kda}</p>
          <p>ADR: {stat.adr}</p>
          {/* Add more stats as needed */}
        </div>
      ))}
    </div>
  );
};

export default CompareStats;
